import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [board, setBoard] = useState([]);

  const [humainCoup, setHumainCoup] = useState([]);
  const [iaCoup, setIaCoup] = useState([]);

  const [turn, setTurn] = useState(0);

  const rows = 6;
  const columns = 7;

  const humain = "yellow";
  const ia = "red";

  // const turn = "humain";

  useEffect(() => {
    let plateau = [];

    for (let i = 0; i < rows; i++) {
      plateau[i] = [];
      for (let j = 0; j < columns; j++) {
        plateau[i][j] = 0;
      }
    }
    setBoard(plateau);
  }, []);

  const handleClick = (e) => {

    let player;

      if ((turn - 1) % 2 === 0) {
        player = 1;
      } else {
        player = 2;
      }

    let row = e.row;
    let column = e.column;

    let getBoard = [...board];

    // let rowLength = getBoard.length;
    let rowLength = 5;
    let columnLength = 6;

    if (board[row][column] === 0) {
      //
      let rowFindCase = [];
      //
      for (let i = 0; i < getBoard.length; i++) {
        rowFindCase = [...rowFindCase, getBoard[i][column]];
      }

      // Check nombre de lignes déjà remplies
      let foundFullRow = getBoard.filter(
        (e) =>
          e[0] !== 0 && 
          e[1] !== 0 &&
          e[2] !== 0 &&
          e[3] !== 0 &&
          e[4] !== 0 &&
          e[5] !== 0 &&
          e[6] !== 0 
      );

      switch (foundFullRow.length) {
        case 0:
          rowLength = 5;
          break;
        case 1:
          rowLength = 4;
          break;
        case 2:
          rowLength = 3;
          break;
        case 3:
          rowLength = 2;
          break;
        case 4:
          rowLength = 1;
          break;
        case 5:
          rowLength = 0;
          break;
        default:
          rowLength = 5;
      }

      console.log("rowLEGTH", player);



      for (let i = 0; i < rowLength; i++) {
        for (let j = 0; j < columnLength; j++) {
          // Last
          if (getBoard[rowLength - i][j] === 0) {
            //
            if (rowFindCase.includes(1)) {
              let howMany1 = rowFindCase.filter((e) => e !== 0);
              row = 5 - howMany1.length;
              getBoard[row][column] = player === 1 ? 1 : 2;
            } else {
              row = rowLength;
              getBoard[rowLength][column] = player === 1 ? 1 : 2;
            }
          }
          //

        }
      }

      setBoard(getBoard);

      setTurn((prevState) => prevState + 1);

      const button = document.getElementById(`button${row}-${column}`);

      if (button) {
        if ((turn - 1) % 2 === 0) {
          //pair
          button.style.backgroundColor = "yellow";
        } else {
          // let rowIA = 3;
          // let columnIA = 5;
          // handleClick({row: rowIA, column: columnIA})
          button.style.backgroundColor = "red";
        }
      }
    }
  };

  useEffect(() => {
    if ((turn - 1) % 2 !== 0) {
      console.log("A MOI")
    }else{

      let getBoard = [...board]

      console.log(getBoard)

      handleClick({row: 2, column: 3});
    }

  }, [turn, board]);

  return (
    <div className="App">
      <h2>Tour : {turn}</h2>
      <br />
      {board.length > 0 &&
        board.map((e, i) => (
          <div key={i} style={{ display: "flex" }}>
            {e.map((f, j) => (
              <button
                id={`button${i}-${j}`}
                style={{
                  width: "70px",
                  height: "70px",
                }}
                key={j}
                value={i}
                name={j}
                onClick={(e) =>
                  handleClick({ row: e.target.value, column: e.target.name })
                }
              ></button>
            ))}
          </div>
        ))}
    </div>
  );
}

export default App;
