import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [board, setBoard] = useState([]);

  const [humainCoup, setHumainCoup] = useState([]);
  const [iaCoup, setIaCoup] = useState([]);

  const [turn, setTurn] = useState(0);

  const rows = 6;
  const columns = 7;

  const humain = "yellow";
  const ia = "red";

  // const turn = "humain";

  useEffect(() => {
    let plateau = [];

    for (let i = 0; i < rows; i++) {
      plateau[i] = [];
      for (let j = 0; j < columns; j++) {
        plateau[i][j] = 0;
      }
    }
    setBoard(plateau);
  }, []);

  const handleClick = (e) => {
    let player;

    if (turn % 2 === 0) {
      console.log("PAIR ?");
      player = 1;
    } else {
      player = 2;
    }

    let row = parseFloat(e.row);
    let column = parseFloat(e.column);

    // const buttonCheck = document.getElementById(`button${row}-${column}`);

    // if (buttonCheck && (buttonCheck.style.backgroundColor === "red" || buttonCheck.style.backgroundColor === "yellow")) {
    //   setTurn(prevState => prevState + 1)
    // }

    let getBoard = [...board];

    // let rowLength = getBoard.length;
    let rowLength = 5;
    let columnLength = 6;

    if (board[row][column] === 0) {
      //
      let rowFindCase = [];
      //
      for (let i = 0; i < getBoard.length; i++) {
        rowFindCase = [...rowFindCase, getBoard[i][column]];
      }

      // Check nombre de lignes déjà remplies
      let foundFullRow = getBoard.filter(
        (e) =>
          e[0] !== 0 &&
          e[1] !== 0 &&
          e[2] !== 0 &&
          e[3] !== 0 &&
          e[4] !== 0 &&
          e[5] !== 0 &&
          e[6] !== 0
      );

      switch (foundFullRow.length) {
        case 0:
          rowLength = 5;
          break;
        case 1:
          rowLength = 4;
          break;
        case 2:
          rowLength = 3;
          break;
        case 3:
          rowLength = 2;
          break;
        case 4:
          rowLength = 1;
          break;
        case 5:
          rowLength = 0;
          break;
        default:
          rowLength = 5;
      }

      for (let i = 0; i < rowLength; i++) {
        for (let j = 0; j < columnLength; j++) {
          // Last
          if (getBoard[rowLength - i][j] === 0) {
            //
            if (rowFindCase.includes(1) || rowFindCase.includes(2)) {
              let howMany1 = rowFindCase.filter((e) => e !== 0);
              row = 5 - howMany1.length;
              getBoard[row][column] = player === 1 ? 1 : 2;
              break;
            } else {
              row = rowLength;
              getBoard[rowLength][column] = player === 1 ? 1 : 2;
              break;
            }
          }

          // if (getBoard[row][column] !== 0) {
          //   i = rowLength;
          //   j = columnLength;
          //   break;
          // }
        }
      }

      setBoard(getBoard);

      setTurn((prevState) => prevState + 1);

      const button = document.getElementById(`button${row}-${column}`);

      if (button) {
        if ((turn - 1) % 2 === 0) {
          //pair
          button.style.backgroundColor = "yellow";
        } else {
          // let rowIA = 3;
          // let columnIA = 5;
          // handleClick({row: rowIA, column: columnIA})
          button.style.backgroundColor = "red";
        }
      }
    }
  };

  useEffect(() => {
    if ((turn - 1) % 2 !== 0) {
      console.log("A MOI");
    } else {
      let getBoard = [...board];

      const getAllIndexes = (arr, val) => {
        var indexes = [],
          i;
        for (i = 0; i < arr.length; i++) if (arr[i] === val) indexes.push(i);
        return indexes;
      };

      let left;
      let right;
      let up;

      // currentRow;
      let upRow;

      let diagonalLT;

      // --------------------------------- Find NEXT MOVE --------------------------------- \\

      function findNextMove() {
        for (let x = 0; x < getBoard.length; x++) {
          if (getBoard[x].includes(2)) {
            let foundIndex = getAllIndexes(getBoard[x], 2);

            for (let i = 0; i < foundIndex.length; i++) {
              //LEFT
              if (getBoard[x][foundIndex[i] - 1] === 0) {
                left = foundIndex[i] - 1;
                upRow = x;
              } else if (
                getBoard[x][foundIndex[i] - 1] !== 0 &&
                getBoard[x][foundIndex[i] - 2] === 0
              ) {
                left = foundIndex[i] - 2;
                upRow = x;
                //RIGHT
              } else if (getBoard[x][foundIndex[i] + 1] === 0) {
                right = foundIndex[i] + 1;
                upRow = x;
              } else if (
                getBoard[x][foundIndex[i] + 1] !== 0 &&
                getBoard[x][foundIndex[i] + 2] === 0
              ) {
                right = foundIndex[i] + 2;
                upRow = x;
                //UP
              } else if (getBoard[x - 1][i] === 0) {
                up = getBoard[x - 1][i];
                upRow = x - 1;
              } else if (getBoard[x - 1][i] !== 0 && getBoard[x - 2][i] === 0) {
                up = getBoard[x - 2][i];
                upRow = x - 2;
              } else if (getBoard[x - 1][i - 1] === 0) {
                diagonalLT = getBoard[x - 1][i - 1];
                upRow = x - 1;
              }
            }
          }
        }
      }

      findNextMove();

      // --------------------------------- DONT LOOSE BRO --------------------------------- \\
      let playerWin;
      for (let x = 0; x < getBoard.length; x++) {
        playerWin = getAllIndexes(getBoard[x], 1);
      }

      console.log("win", playerWin)

      // ----------------------------------------------------------------------------------- \\

      console.log("left", left);
      console.log("right", right);
      console.log("up", up);
      console.log("row", upRow);
      console.log("diagonal left top", diagonalLT);

      if ((left || right || up || diagonalLT) && upRow) {
        handleClick({ row: upRow, column: left || right || up || diagonalLT });
      } else if (!left || !right || !up) {
        handleClick({ row: 0, column: 3 });
      }
    }
  }, [turn, board]);

  return (
    <div className="App">
      <h2>Tour : {turn}</h2>
      <br />
      {board.length > 0 &&
        board.map((e, i) => (
          <div key={i} style={{ display: "flex" }}>
            {e.map((f, j) => (
              <button
                id={`button${i}-${j}`}
                style={{
                  width: "70px",
                  height: "70px",
                }}
                key={j}
                value={i}
                name={j}
                onClick={(e) =>
                  handleClick({ row: e.target.value, column: e.target.name })
                }
              ></button>
            ))}
          </div>
        ))}
    </div>
  );
}

export default App;
