import { useState, useEffect } from "react";
import "./App.css";

function App() {
  const [board, setBoard] = useState([]);

  const [humainCoup, setHumainCoup] = useState([]);
  const [iaCoup, setIaCoup] = useState([]);

  const [turn, setTurn] = useState(0);

  const rows = 6;
  const columns = 7;

  const humain = "yellow";
  const ia = "red";

  // const turn = "humain";

  useEffect(() => {
    let plateau = [];

    for (let i = 0; i < rows; i++) {
      plateau[i] = [];
      for (let j = 0; j < columns; j++) {
        plateau[i][j] = 0;
      }
    }
    setBoard(plateau);
  }, []);

  const handleClick = (e) => {
    let row = e.target.value;
    let column = e.target.name;

    let getBoard = [...board];

    if (board[row][column] === 0) {

      for (let i = 0; i < getBoard[5].length; i++) {
        if(getBoard[5][i] !== 1){
          row = 5;
          getBoard[5][column] = 1;

        }
      }

      setBoard(getBoard);

      setTurn((prevState) => prevState + 1);

      const button = document.getElementById(`button${row}-${column}`);

      if (button) {
        if ((turn - 1) % 2 === 0) {
          button.style.backgroundColor = "yellow";
        } else {
          button.style.backgroundColor = "red";
        }
      }
    }
  };

  return (
    <div className="App">
      <h2>Tour : {turn}</h2>
      <br />
      {board.length > 0 &&
        board.map((e, i) => (
          <div key={i} style={{ display: "flex" }}>
            {e.map((f, j) => (
              <button
                id={`button${i}-${j}`}
                style={{
                  width: "70px",
                  height: "70px",
                }}
                key={j}
                value={i}
                name={j}
                onClick={handleClick}
              ></button>
            ))}
          </div>
        ))}
    </div>
  );
}

export default App;
