import {useState, useEffect} from "react";
import "./App.css";


function App() {

 const [board, setBoard] = useState([])


  const rows = 6;
  const columns = 6;


  useEffect(() => {
    let plateau = [];

    for (let i = 0; i < rows; i++) {
      plateau[i] = [];
        for (let j = 0; j < columns; j++) {
          plateau[i][j] = 0
        }
  }
  setBoard(plateau)
  }, [])

  const handleClick = (e) => {
    let row = e.target.value;
    let column = e.target.name;

    let getBoard = [...board];
    board[row][column] = 1;
    setBoard(getBoard)


    console.log(board)
  }




  return <div className="App">
    {board.length > 0 && board.map((e, i) => { 
      return e.map((x,y) => (
        <button style={{width:'86px', height:'100px'}} key={y} name={y} value={i} onClick={handleClick}></button>
      ))

    })}
  </div>;
}

export default App;
