import "./App.css";


function App() {

 const [board, setBoard] = useState([])

 const [humainCoup, setHumainCoup] = useState([])
 const [iaCoup, setIaCoup] = useState([])

 const [turn, setTurn] = useState(0)


  const rows = 6;
  const columns = 6;

  const humain = "yellow";
  const ia = "red";

  // const turn = "humain";


  useEffect(() => {
    let plateau = [];

    for (let i = 0; i < rows; i++) {
      plateau[i] = [];
        for (let j = 0; j < columns; j++) {
          plateau[i][j] = 0
        }
  }
  setBoard(plateau)
  }, [])

  const handleClick = (e) => {
    let row = e.target.value;
    let column = e.target.name;

    let getBoard = [...board];
    board[row][column] = 1;
    setBoard(getBoard)


    setTurn(prevState => prevState + 1)

    if(turn%2 === 0)
    {
           alert("Nombre pair");
    }
    else
    {
           alert("Nombre impair");
    }


    console.log(board)
  }

  useEffect(() => {



  }, [board])




  return <div className="App">

    <h2>Tour : {turn}</h2>
    <br/>
    {board.length > 0 && board.map((e, i) => { 
      return e.map((f,j) => (
        <button style={{width:'86px', height:'100px', background: board[i][j] === 1 ?  "red" : "transparent"}} key={j} value={i} name={j} onClick={handleClick}></button>
      ))

    })}
  </div>;
}

export default App;
